@OptIn(ExperimentalMaterial3Api::class)
@Composable
fun ResultScreen(
    questions: List<Question>,
    answers: Map<String, UserAnswer>,
    timeTaken: Long,
    mode: String,
    subject: String?,
    unit: String?,
    gsPaper: String?,
    onViewSolutions: () -> Unit,
    onRetakeTest: () -> Unit,
    onHome: () -> Unit
) {
    val context = androidx.compose.ui.platform.LocalContext.current
    val scope = rememberCoroutineScope()

    // Calculate results
    val result = remember {
        calculateResult(questions, answers)
    }

    // Save to database
    LaunchedEffect(Unit) {
        scope.launch {
            withContext(Dispatchers.IO) {
                saveTestAttempt(
                    context = context,
                    questions = questions,
                    answers = answers,
                    result = result,
