            )
        },
        containerColor = BackgroundDark
    ) { paddingValues ->
        LazyColumn(
            modifier = Modifier
                .fillMaxSize()
                .background(
                    brush = Brush.verticalGradient(
                        colors = listOf(BackgroundDark, SurfaceDark, BackgroundDark)
                    )
                )
                .padding(paddingValues)
                .padding(16.dp),
            verticalArrangement = Arrangement.spacedBy(16.dp)
        ) {
            // Score Card
            item {
                ScoreCard(result = result, timeTaken = timeTaken)
            }

            // Statistics Card
            item {
                StatisticsCard(result = result, totalQuestions = questions.size)
            }

            // Performance Chart
            item {
                PerformanceChart(result = result)
            }

            // Action Buttons
            item {
                ActionButtons(
                    onViewSolutions = onViewSolutions,
                    onRetakeTest = onRetakeTest,
                    onHome = onHome
                )
            }
        }
    }
}

@Composable
fun ScoreCard(result: ResultData, timeTaken: Long) {
    Card(
        modifier = Modifier.fillMaxWidth(),
        shape = RoundedCornerShape(16.dp),
        colors = CardDefaults.cardColors(
            containerColor = CardBackground
        ),
        elevation = CardDefaults.cardElevation(defaultElevation = 4.dp)
    ) {
        Column(
            modifier = Modifier
                .fillMaxWidth()
                .padding(24.dp),
            horizontalAlignment = Alignment.CenterHorizontally
        ) {
            // Score circle
            Box(
                modifier = Modifier
                    .size(120.dp)
                    .background(
                        brush = Brush.radialGradient(
                            colors = listOf(GradientStart, GradientEnd)
                        ),
                        shape = RoundedCornerShape(60.dp)
                    ),
                contentAlignment = Alignment.Center
            ) {
                Column(horizontalAlignment = Alignment.CenterHorizontally) {
                    Text(
                        text = "${result.score}",
                        fontSize = 32.sp,
                        fontWeight = FontWeight.Bold,
                        color = TextPrimary
                    )
                    Text(
                        text = "/ 30",
                        fontSize = 16.sp,
                        color = TextSecondary
                    )
                }
            }

            Spacer(modifier = Modifier.height(16.dp))

            Text(
                text = "${result.percentage.toInt()}%",
                fontSize = 36.sp,
                fontWeight = FontWeight.Bold,
                color = when {
                    result.percentage >= 75 -> StatGreen
                    result.percentage >= 50 -> StatOrange
                    else -> Color.Red
                }
            )

            Spacer(modifier = Modifier.height(8.dp))

            Text(
                text = "Time Taken: ${formatTime(timeTaken)}",
                fontSize = 14.sp,
                color = TextSecondary
            )
        }
    }
}

@Composable
fun StatisticsCard(result: ResultData, totalQuestions: Int) {
    Card(
        modifier = Modifier.fillMaxWidth(),
        shape = RoundedCornerShape(16.dp),
        colors = CardDefaults.cardColors(containerColor = CardBackground),
        elevation = CardDefaults.cardElevation(defaultElevation = 4.dp)
    ) {
        Column(
            modifier = Modifier
                .fillMaxWidth()
                .padding(16.dp)
        ) {
            Text(
                text = "Statistics",
                fontSize = 18.sp,
                fontWeight = FontWeight.Bold,
                color = TextPrimary
            )

            Spacer(modifier = Modifier.height(16.dp))

            Row(
                modifier = Modifier.fillMaxWidth(),
                horizontalArrangement = Arrangement.SpaceEvenly
            ) {
                StatColumn(
                    label = "Correct",
                    value = result.correctAnswers.toString(),
                    color = StatGreen
                )
                StatColumn(
                    label = "Wrong",
                    value = result.wrongAnswers.toString(),
                    color = Color.Red
                )
                StatColumn(
                    label = "Skipped",
                    value = result.skippedAnswers.toString(),
                    color = TextSecondary
                )
            }

            Spacer(modifier = Modifier.height(16.dp))

            // Progress bars
            ResultProgressBar(
                label = "Correct",
                count = result.correctAnswers,
                total = totalQuestions,
                color = StatGreen
            )
            Spacer(modifier = Modifier.height(8.dp))
            ResultProgressBar(
                label = "Wrong",
                count = result.wrongAnswers,
                total = totalQuestions,
                color = Color.Red
            )
            Spacer(modifier = Modifier.height(8.dp))
            ResultProgressBar(
                label = "Skipped",
                count = result.skippedAnswers,
                total = totalQuestions,
                color = TextSecondary
            )
        }
    }
}

@Composable
fun StatColumn(label: String, value: String, color: Color) {
    Column(horizontalAlignment = Alignment.CenterHorizontally) {
        Text(
            text = value,
            fontSize = 28.sp,
            fontWeight = FontWeight.Bold,
            color = color
        )
        Text(
            text = label,
            fontSize = 14.sp,
            color = TextSecondary
        )
    }
}

@Composable
fun ResultProgressBar(label: String, count: Int, total: Int, color: Color) {
    Column {
        Row(
            modifier = Modifier.fillMaxWidth(),
            horizontalArrangement = Arrangement.SpaceBetween
        ) {
            Text(
                text = label,
                fontSize = 14.sp,
                color = TextPrimary
            )
            Text(
                text = "$count/$total",
                fontSize = 14.sp,
                color = TextSecondary
            )
        }
        Spacer(modifier = Modifier.height(4.dp))
        LinearProgressIndicator(
            progress = { count.toFloat() / total.toFloat() },
            modifier = Modifier
                .fillMaxWidth()
                .height(8.dp),
            color = color,
            trackColor = SurfaceDark,
        )
    }
}

